import Vue from 'vue'
import VueRouter from 'vue-router'
import Body from '../components/Body.vue';
import dashboard from '../pages/dashboard.vue';
import Auth from '../pages/authentication/index.vue';
import Profile from '../pages/profiles/profile.vue';
import Feedback from '../pages/profiles/change.vue';

Vue.use(VueRouter)

const routes = [
    { path: '', redirect: { name: 'default' } },
    {
        path: '/dashboard',
        component: Body,
        children: [{
            path: 'default',
            name: 'default',
            component: dashboard,
            meta: {
                title: 'Multikart - Premium Admin Template',
            }
        }]
    },

    {
        path: '/settings',
        name: 'settings',
        component: Body,
        children: [{
            path: 'profile',
            name: 'profile',
            component: Profile,
            meta: {
                title: 'Profile | Multikart - Premium Admin Template',

            }
        }]
    },

    {
        path: '/feedback',
        name: 'feedback',
        component: Body,
        children: [{
            path: 'change-info',
            name: 'change-info',
            component: Feedback,
            meta: {
                title: 'Profile | Multikart - Premium Admin Template',

            }
        }, {
            path: 'change-info',
            name: 'change-info',
            component: Feedback,
            meta: {
                title: 'Profile | Multikart - Premium Admin Template',

            }
        }]
    },


    {
        path: '/auth/login',
        name: 'login',
        component: Auth,
        meta: {
            title: 'Login | Multikart - Premium Admin Template',

        }
    },

]


const router = new VueRouter({
    mode: 'hash',
    /* base: process.env.BASE_URL, */
    routes
})

router.beforeEach((to, from, next) => {
    const publicPages = ['/auth/login', '/dashboard/default'];
    const authRequired = !publicPages.includes(to.path);
    const loggedIn = localStorage.getItem('user');
    // trying to access a restricted page + not logged in
    // redirect to login page
    if (authRequired && !loggedIn) {
        next('/auth/login');
    } else {
        next();
    }
});

export default router
